mapper.insert:
INSERT INTO BOARD
     (
	   BOARD_NO
     , BOARD_TITLE
     , BOARD_CONTENT
     , BOARD_USER_ID
     , BOARD_REGDATE
     ) 
VALUES 
     (
       (SELECT NVL(MAX(BOARD_NO), 0)+1 FROM BOARD)
     , #{boardTitle}
     , #{boardContent}
     , #{boardUserId}
     , SYSDATE
     )

mapper.delete:
DELETE FROM BOARD
 WHERE 1 = 1
   AND BOARD_NO = #{boardNo}

mapper.update:
UPDATE BOARD SET
       BOARD_TITLE    = #{boardTitle}
     , BOARD_CONTENT  = #{boardContent}
     , BOARD_USER_ID  = #{boardUserId}
     , BOARD_REGDATE  = SYSDATE
 WHERE 1 = 1
   AND BOARD_NO = #{boardNo}
